import{_ as o,W as t,X as p,$ as i,Y as e,Z as a,a2 as c,C as r}from"./framework-aa83d01c.js";const l={},d=e("h2",{id:"ioc概念及原理",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#ioc概念及原理","aria-hidden":"true"},"#"),a(" IOC概念及原理")],-1),s=e("p",null,"Spring 是 Java 项目框架中最流行的一个。",-1),C=e("p",null,"Spring 容器最核心的两个概念是:",-1),_=e("ul",null,[e("li",null,"IOC: (Inversion Of Control，控制反转) 是核心概念，而 DI(Dependency Injection，依赖注入) 是其实现方式，也是 IOC 的另一种称呼。"),e("li",null,"AOP: (Aspect-Oriented Programming，面向切面编程) 是 Spring 框架的另一个重要的概念。")],-1),h=c('<p>在传统的编程模型中，我们在一个类中通过<code>new</code>关键字实例化另一个类的对象, 这样就会造成两个类之间的强耦合关系，使得代码难以扩展、维护和测试。</p><p>而在 Spring 框架中，采用 IOC 的设计模式，将对象的创建和依赖管理交给 Spring 容器来完成，从而实现了解耦。</p><p>在 Spring 中，IOC 的实现是通过依赖注入（DI）来实现的。它通过将分析对象的依赖关系将依赖注入到对象中，从而实现对象创建的解耦。在 Spring 中，可以使用三种方式来实现 DI：</p><ol><li>构造函数注入：通过在类的构造函数中定义参数，然后在 XML 配置文件或者使用注解的方式来注入依赖对象。</li><li>属性注入：通过在类中定义成员变量，然后在 XML 配置文件或者使用注解的方式来注入依赖对象。</li><li>接口注入：通过在类中定义接口类型和成员变量，然后在 XML 配置文件或者使用注解的方式来注入依赖对象。</li></ol><p>除了依赖注入之外，Spring 还提供了依赖查找（Dependency Lookup）的机制，可以让我们在需要使用依赖对象时，从 Spring 容器中查找对应的 Bean 对象。依赖查找和依赖注入一起构成了 Spring 框架的 IOC 容器机制。</p><p>总之，通过 IOC 容器，Spring 将对象之间的依赖关系解耦，提高了代码的灵活性、可扩展性和可维护性，从而使得我们的应用程序更加易开发和测试。将开发人员从繁琐的对象构建过程释放出来。</p><h2 id="ioc容器的类型" tabindex="-1"><a class="header-anchor" href="#ioc容器的类型" aria-hidden="true">#</a> IOC容器的类型</h2><p>在 Spring 框架中，IOC容器有三种类型：</p><ol><li><p><code>BeanFactory</code>：是Spring框架的基础接口，提供了IOC容器的基本功能。它通过Lazy Loading的方式来创建和管理Bean实例，即只有在第一次使用时才会创建Bean实例。BeanFactory的优点是轻量级，适合在资源受限的环境中使用。</p></li><li><p><code>ApplicationContext</code>：是BeanFactory的子接口，它是Spring框架中最常用的IOC容器。与BeanFactory相比，ApplicationContext提供了更多的功能，例如国际化、事件处理、AOP等。ApplicationContext在启动时会一次性创建所有Bean实例，这样可以提高应用程序的性能。</p></li><li><p><code>WebApplicationContext</code>：是ApplicationContext的子接口，它是Web应用程序中使用的IOC容器。WebApplicationContext可以获取ServletContext中的参数、属性和资源，并可以访问Servlet API中的对象。它还提供了许多与Web相关的功能，例如Handler Mapping和View Resolver等。</p></li></ol><p>在实际开发中，我们可以根据应用程序的需要来选择合适的IOC容器类型。如果应用程序的资源受限，可以选择BeanFactory；如果应用程序需要更多的功能，例如AOP、国际化等，可以选择ApplicationContext；如果应用程序是Web应用程序，可以选择WebApplicationContext。</p><p>通常如果我们使用 SpringBoot 项目，基本都是使用的 ApplicationContext，而如果是传统 Spring 项目，需要搭配外部 Web 容器来使用的话，使用的是 WebApplicationContext。</p>',11),g={class:"hint-container tip"},x=e("p",{class:"hint-container-title"},"容器继承图",-1);function A(B,I){const n=r("Mermaid");return t(),p("div",null,[d,s,C,_,i(n,{id:"mermaid-21",code:"eJzLzcxLyU0s4FJQKMrPL9HQCC4oysxLf7+n49mCHU/3Nz9b1vR0/9b3ezo1NYFKFBQ8/Z1Bcn3Ln3Zse9rf+2LvGqAcWMbRPwAo82Tf3Bdbpz3bvOJp61KQDAC9BDI2"}),h,e("div",g,[x,i(n,{id:"mermaid-84",code:"eJxLL0osyFBwCuFSUHAsKMjJTE4syczPc87PK0mtKFHQ1VV4vnv5s879QJadglNqYp5bYnJJflElUHl4ahJBHZgKuABQCCqw"})])])}const O=o(l,[["render",A],["__file","index.html.vue"]]);export{O as default};
